(function(){Vue.component("convos-dialog-container",{props:["dialog","user"],mixins:[Convos.mixin.messages],methods:{draw:function(e){this.$els.main.style.bottom=this.$els.input.offsetHeight+"px";this.scrollToBottom({});}},template:"<div class=\"convos-dialog-container\">\n    <header>\n      <convos-toggle-main-menu :user=\"user\"></convos-toggle-main-menu>\n      <h2 v-tooltip=\"dialog.topic || &#39;No topic is set.&#39;\">{{user.ws.is(&#39;open&#39;) ? dialog.name || &#39;Convos&#39; : &#39;No internet connection?&#39;}}</h2>\n      <convos-header-links :toggle=\"true\" :user=\"user\"></convos-header-links>\n    </header>\n    <main class=\"scroll-element\" v-el:main>\n      <component :dialog=\"dialog\" :is=\"&#39;convos-message-&#39; + msg.type\" :msg=\"msg\" :user=\"user\" v-for=\"msg in dialog.messages\" v-if=\"msg.type\"></component>\n    </main>\n    <convos-input :dialog=\"dialog\" :user=\"user\" @resized=\"draw\" v-el:input></convos-input>\n  </div>\n"})})();