(function(){Vue.component("convos-dialog-settings",{props:["user"],data:function(){return{modes:{'@':'+o','+':'+v'}};},computed:{dialog:function(){return this.user.getActiveDialog();},participants:function(){if(!this.dialog)return[];return Object.values(this.dialog.participants).sort(function(a,b){return a.name.toLowerCase()>b.name.toLowerCase();});}},methods:{closeTooltip:function(){return this.dialog.is_private?'Close conversation':'Part channel';}},template:"<div class=\"convos-dialog-settings is-sidebar\">\n    <header><convos-header-links :user=\"user\"></convos-header-links></header>\n    <div class=\"content\">\n      <div class=\"row\">\n        <div class=\"col s12\">\n          <h5>\n            <a @click.prevent=\"send(&#39;/close&#39;)\" class=\"btn-floating waves-effect waves-light green\" href=\"#close\" v-if=\"dialog\" v-tooltip=\"closeTooltip()\"><i class=\"material-icons\">close</i></a>\n            Topic\n          </h5>\n          <p>{{dialog ? dialog.topic || &#39;No topic is set.&#39; : &#39;No active dialog.&#39;}}</p>\n        </div>\n      </div>\n      <div class=\"row\" v-if=\"dialog\">\n        <div class=\"col s12\">\n          <h5>Participants ({{participants.length}})</h5>\n        </div>\n      </div>\n      <div class=\"row participant\" v-for=\"p in participants\">\n        <div class=\"col s12\">\n          <span class=\"secondary-content ts\">\n            {{modes[p.mode] || p.mode}}\n            <a @click.prevent=\"send(&#39;/whois &#39; + p.name)\" class=\"waves-effect waves-light\" href=\"#info\"><i class=\"material-icons\">info</i></a>\n          </span>\n          <a @click.prevent=\"send(&#39;/query &#39; + p.name)\" href=\"#chat:{{p.name}}\">{{p.name}}</a>\n        </div>\n      </div>\n    </div>\n  </div>\n"})})();