(function(){Vue.component("convos-message-whois",{props:["dialog","msg","user"],data:function(){return{channels:Object.keys(this.msg.channels)};},methods:{join:function(name){var connection=this.dialog.connection();var dialog=connection.getDialog(name);if(dialog)return this.settings.main=dialog.href();connection.send('/join '+name,this.dialog);}},template:"<div class=\"convos-message notice\">\n    <a class=\"title\" href=\"#chat\">{{msg.from}}</a>\n    <div class=\"message\">\n      <abbr v-tooltip.literal=\"{{msg.name}}\">{{msg.nick}}</abbr>\n      <template v-if=\"msg.idle_for\">has been idle for {{msg.idle_for}} seconds in</template>\n      <template v-else=\"msg.idle_for\">is active in</template>\n      <template v-for=\"name in channels\">\n        {{!$index ? &#39;&#39; : $index + 1 == channels.length ? &quot;and&quot; : &quot;, &quot;}}\n        <a @click.prevent=\"join(name)\" href=\"#join:{{name}}\">{{name}}</a>\n      </template>.\n    </div>\n    <span class=\"secondary-content ts\" v-tooltip=\"msg.ts.toLocaleString()\">{{msg.ts | timestring}}</span>\n  </div>\n"})})();