(function(){Vue.component("convos-profile",{props:["user"],data:function(){return{errors:[],notifications:Notification.permission};},methods:{save:function(){if(this.notifications&&this.notifications!=this.settings.notifications){this.enableNotifications(true);}
if(!this.notifications){this.enableNotifications(false);}}},template:"<div class=\"convos-profile is-sidebar\">\n    <header>\n      <convos-toggle-main-menu :user=\"user\"></convos-toggle-main-menu>\n      <convos-header-links :user=\"user\"></convos-header-links>\n    </header>\n    <div class=\"content\">\n      <div class=\"row\">\n        <div class=\"col s12\">\n          <h5 class=\"truncate\">{{user.email}}</h5>\n        </div>\n      </div>\n      <div class=\"row\">\n        <div class=\"input-field col s12\">\n          <input class=\"validate\" id=\"form_password\" placeholder=\"At least six characters\" type=\"password\">\n          <label for=\"form_password\">Password</label>\n        </div>\n        <div class=\"input-field col s12\">\n          <input class=\"validate\" id=\"form_password_again\" placeholder=\"Repeat password\" type=\"password\">\n        </div>\n      </div>\n      <div class=\"row\">\n        <div class=\"col s12\">\n          <input :checked=\"settings.notifications == &#39;granted&#39;\" class=\"filled-in\" id=\"form_notifications\" type=\"checkbox\" v-model=\"notifications\" value=\"granted\">\n          <label for=\"form_notifications\">Enable notifications</label>\n        </div>\n      </div>\n      <div class=\"row\" v-if=\"errors.length\">\n        <div class=\"col s12\"><div class=\"alert\">{{errors[0].message}}</div></div>\n      </div>\n      <div class=\"row\">\n        <div class=\"input-field col s12\">\n          <button @click=\"save\" class=\"btn waves-effect waves-light\" type=\"submit\">Update <i class=\"material-icons right\">save</i></button>\n        </div>\n      </div>\n    </div>\n  </div>\n"})})();