(function(){var codeToHtmlRe=new RegExp("(\\\\?)`([^`]+)`","g");var mdToHtmlRe=new RegExp("(^|\\s)(\\\\?)(\\*+|_+)(\\w.*?)\\3","g");Vue.filter("markdown",function(str,args){if(!args)args={emoji:true};if(args.escape)str=str.xmlEscape();str=str.replace(mdToHtmlRe,function(all,b,esc,md,text){if(md.match(/^_/)&&text.match(/^[A-Z]+$/))return all;switch(md.length){case 1:return esc?all.replace(/^\\/,""):b+"<em>"+text+"</em>";case 2:return esc?all.replace(/^\\/,""):b+"<strong>"+text+"</strong>";case 3:return esc?all.replace(/^\\/,""):b+"<em><strong>"+text+"</strong></em>";default:return all;}});str=str.replace(codeToHtmlRe,function(all,esc,text){return esc?all.replace(/^\\/,""):"<code>"+text+"</code>";});if(args.emoji)str=emojione.toImage(str);if(args.links)str=str.autoLink({target:"_blank"});return str;});})();