(function(){Vue.component("md-option",{props:["disabled","selected","value"],methods:{text:function(){if(!this.$el)return;return this.$el.querySelector('span').innerText.trim();},select:function(){if(this.disabled)return;this.selected=true;this.$parent.$emit('option::selected',this);}},beforeDestroy:function(){this.$parent.$emit('option::removed',this);},ready:function(){this.$parent.$emit('option::added',this);},template:"<li @click=\"select\" class=\"{{selected ? &#39;active&#39; : &#39;&#39;}}\">\n    <span>\n      <input :checked=\"selected\" :disabled=\"disabled\" type=\"checkbox\" v-if=\"$parent.multiple\">\n      <slot>{{value}}</slot>\n    </span>\n  </li>\n"})})();